
//extracts characters from http://unicode.org/charts/charindex.html
//needs jquery to run

//used for decoding usernames for mention list when typing a chat message (last seen on line 1746)


var library = {};

$("body > form > table > tbody > tr:nth-child(3) > td:nth-child(2) tr tr").each(function(i, e) {
	e = $(e).children();
    var name = e.eq(0).text();
    var value = e.eq(1).text().trim();
    if (!value) return;
    var matches = name.match(/(?: |-|^)[a-z0-9](?: |-|$)/gi);
    if (!matches) return;
    matches.forEach(function(a) {
    	a = a.match(/[a-z0-9]/i)[0].toLowerCase();
    	if (library[a])
    		library[a].push(value);
    	else
    		library[a] = [value];
    });
});

console.log(JSON.stringify(library));









