function q(chars) {
    add(chars[0], chars[1]);
}
function add(letter, code) {
    a = code.charCodeAt(0).toString(16);
    a = "000".substring(0, 4 - a.length) + a.toUpperCase();
    if (library[letter]) {
        if (library[letter].indexOf(a) == -1) {
            library[letter].push(a);
        }
    } else
        library[letter] = [a];
}
$("#textin").on("input", function() {
    var letter = $("#textin").val()[0];
    //console.log(letter);
    if (/\d/.test(letter))
        $("#textin").val(letter);
    else
        $("#textin").val(letter + letter.toUpperCase());
    $("li pre").text().split("").forEach(function(a){
        add(letter, a);
    });
});
"abcdefghijklmopqrstuvwxyz0123456789".split("").forEach(function(letter) {
    if (/\d/.test(letter))
        $("#textin").val(letter);
    else
        $("#textin").val(letter + letter.toUpperCase());
    $("#textin").trigger("input");
});
